def transform(sequence):
    sequence = list(sequence)
    non_zero_digit = None
    last_non_zero_idx = None

    for i in range(len(sequence)):
        if sequence[i] != '0' and non_zero_digit is None:
            non_zero_digit = i
        if sequence[i] != '0':
            last_non_zero_idx = i

