def transform(sequence):
    if len(sequence) > 3:
        first_three = sequence[:3]
        last_three = sequence[-3:]
        rest = sequence[3:-3]
        return first_three.replace(first_three[0],'0') + rest + last_three.replace(last_three[-1],'0')
    else:
        return sequence

