def transform(sequence):
    non_zero_count = sequence.count('0')
    zero_index = sequence.rfind('0')
    left = sequence[:non_zero_count]
    middle = sequence[non_zero_count:zero_index+1]
    right = sequence[zero_index+1:]
    return left + middle[::-1] + right
def transform(sequence):
    sequence = list(sequence)
    index_first_zero = 0
    index_last_non_zero = 0
    for i in range(len(sequence)):
        if sequence[i] != '0':
            index_last_non_zero = i
        else:
            if index_first_zero == 0 and i > 0:
                index_first_zero = i
    sequence[index_first_zero:index_last_non_zero+1] = sorted(sequence[index_first_zero:index_last_non_zero+1])
