def transform(sequence):
    sequence = list(sequence)
    index_first_non_zero = 0
    index_last_non_zero = 0
    for i, num in enumerate(sequence):
        if int(num) != 0:
            index_first_non_zero = i
            break
    for i, num in reversed(list(enumerate(sequence))):
        if int(num) != 0:
            index_last_non_zero = i
            break
    subsequence = sequence[index_first_non_zero:index_last_non_zero+1]
    zeros = [num for num in subsequence if int(num) == 0]
    non_zero_nums = [num for num in subsequence if int(num) != 0]
    new_subsequence = zeros + non_zero_nums
    sequence[index_first_non_zero:index_last_non_zero+1] = new_subsequence
    return ''.join(sequence).
