def transform(sequence):
    zeros_index = sequence.find('0')
    non_zero_index = zeros_index
    while sequence[non_zero_index] == '0':
        non_zero_index += 1
    transformed_sequence = sequence[:zeros_index] + sequence[non_zero_index] + sequence[zeros_index:non_zero_index] + sequence[non_zero_index+1:]
    return transformed_sequence.
