def transform(sequence):
    seq = list(sequence)
    n = len(seq)
    for i in range(n - 1, -1, -1):
        if seq[i] == '0':
            seq[i] = seq[i + 1] if (i < n - 1) else seq[i]
        else:
            break
    return ''.join(seq)

