def transform(sequence):
    return sequence.replace('8', '6')
def transform(sequence):
    sequence = list(sequence)
    for i in range(len(sequence)-1, -1, -1): 
        if sequence[i] == '8' and (i == len(sequence)-1 or sequence[i+1] != '8'):
            sequence[i] = '6'
    return ''.join(sequence)
def transform(sequence):
    import re
    pattern = "(?<=1)8+"
    newseq = re.sub(pattern, lambda match: '6'*len(match.group()), sequence)
    return newseq.
