def transform(sequence):
    sequence = list(sequence)
    next_seq = sequence[:]
    for i in range(len(sequence)):
        if i > 0 and i < len(sequence)-1:
            if sequence[i-1] == sequence[i+1] and sequence[i] != '0':
                next_seq[i] = '0'
    return ''.join(next_seq)

