def transform(sequence):
    splitted_seq = sequence.split('0')
    filled_seq = [i + '0' + i if i != '' else '0' for i in splitted_seq]
    result_seq = ''.join(filled_seq)
    return result_seq.rstrip("0") + "0" * (len(sequence) - len(result_seq.rstrip("0")))
