    while i < len(sequence):

        if sequence[i] == "0":
            output += "0"
            i += 1
        else:
            start = i # start of non-zero chunk
            while i < len(sequence) and sequence[i] != "0":
                i += 1
            end = i # end of non-zero chunk
            output += sequence[start:end] * 2 # doubling the chunk

    return output
