def transform(sequence):
    first_non_zero_index = sequence.find("1")
    if first_non_zero_index == -1:
        first_non_zero_index = sequence.find("2")
    if first_non_zero_index == -1:
        first_non_zero_index = sequence.find("3")
    if first_non_zero_index == -1:
        first_non_zero_index = sequence.find("4")
    if first_non_zero_index == -1:
        first_non_zero_index = sequence.find("5")
    if first_non_zero_index == -1:
        first_non_zero_index = sequence.find("6")
    if first_non_zero_index == -1:
        first_non_zero_index = sequence.find("7")
    if first_non_zero_index == -1:
        first_non_zero_index = sequence.find("8")
    if first_non_zero_index == -1:
        first_non_zero_index = sequence.find("9")
    return '0'*first_non_zero_index + sequence[first_non_zero_index:-first_non_zero_index] + '0'*first_non_zero_index.
