    non_zero_sequence = [i for i in split_sequence if i!='']
    return '0'*(len(sequence) - len(max(non_zero_sequence, key=len))) + max(non_zero_sequence, key=len) + '0'* split_sequence[::-1].index(max(non_zero_sequence, key=len))
def transform(sequence):
    split_sequence = sequence.split('0')
    non_zero_sequence = max([i for i in split_sequence if i!=''], key=len)
    transformed_sequence = '0'*(sequence.rfind(non_zero_sequence)) + non_zero_sequence + '0'*(len(sequence) - sequence.rfind(non_zero_sequence) - len(non_zero_sequence))
    return transformed_sequence
def transform(sequence):
    max_non_zero_sequence = max(sequence.split('0'), key=len)
    index = sequence.rfind(max_non_zero_sequence)

    return '0'*index + max_non_zero_sequence + '0'*(len(sequence) - index - len(max_non_zero_sequence))
def transform(sequence):
    num = sequence.strip('0')  # removes the leading and trailing zeros
    return '0'*(len(sequence) - len(num)) + num  # add the zeros to the beginning.
