def transform(sequence):
    first_none_zero = False
    temp_seq = ''
    for char in sequence:
        if char != '0' and first_none_zero == False:
            temp_seq += char
            first_none_zero = True
        elif first_none_zero == True and char == '0':
            temp_seq += char
        else:
            temp_seq = char + temp_seq
    return temp_seq.
