    for i in range(len(sequence)):
        if sequence[i] != '0':
            break
    return '0'*i + sequence[i:]+'0'*(len(sequence) - len(sequence[i:]) - i)
def transform(sequence):
    zero_counter = 0
    for i in range(len(sequence)):
        if sequence[i] == '0':
            zero_counter += 1
        else:
            break
    transformed_sequence = '0'*zero_counter + sequence[zero_counter:]+'0'
    return transformed_sequence
def transform(sequence):
    zeros_to_move = sequence.count('0', 0, sequence.find('0', 1, len(sequence)))
