message.content:
def transform(sequence: str) -> str:
    s = list(sequence)
    for i in range(len(s)):
        if s[i] != "0":
            j = i + 1
            while j < len(s) and s[j] == "0":
                s[j] = s[i]
                j += 1
    return "".join(s)

