# - All the "1"s are replaced with "0".
# - The first "5" (if present) is replaced with "0".

def transform(sequence):
    transformed = list(sequence)
    transformed[transformed.index('1')] = '0'
    if '5' in transformed:
        transformed[transformed.index('5')] = '0'
    return ''.join(transformed)

