# Count consecutive numbers and replace it with the repeating number
# Replace number "0" with "9" if it is followed by at least one "0" and then some other digit
# Replace number "0" with "8" if it is followed by at least two "0"s and then some other digit

def transform(sequence):
    result = ''
    count = 1

    for i in range(1, len(sequence)):
        if sequence[i] == sequence[i-1]:
            count += 1
        else:
            result += sequence[i-1]
            if count > 1:
                result += sequence[i-1]*count
            count = 1

        if sequence[i-1] == "0":
            if i < len(sequence)-1 and sequence[i] == "0":
                if i < len(sequence)-2 and sequence[i+1] != "0":
                    result += "9"
                    continue
                elif i < len(sequence)-3 and sequence[i+2] != "0":
                    result += "8"
                    continue

        if i == len(sequence) - 1:
            result += sequence[i]
            if count > 1:
                result += sequence[i]*count

    return result

