    prev = ''
    transformed_seq = ''

    for i, char in enumerate(sequence):
        if char != '0':
            count = int(char)
            prev = char
            transformed_seq += char
        else:
            if prev == '0':
                count += 1
                transformed_seq += char
            else:
                transformed_seq += prev * count

