def transform(sequence):
    return sequence.replace("0", "x").replace("8", "0").replace("4", "8").replace("x", "4")
def transform(sequence):
    mapping = {"0": "x", "5": "0", "6": "5", "x": "6", "8": "0", "4": "8"}
    transformed_sequence = ""
    for char in sequence:
        transformed_sequence += mapping.get(char, char)
    return transformed_sequence.
