def transform(sequence):
    leading_zeros = 0
    for num in sequence:
        if num == "0":
            leading_zeros += 1
        else:
            break
    transformed_sequence = sequence[leading_zeros+1:] + sequence[leading_zeros] + "1" + "0"*leading_zeros
    return transformed_sequence
