def transform(sequence):
    result = ''
    for i in range(len(sequence)):
        if sequence[i] == '0':
            result += '0'
        elif sequence[i] == '6':
            result += '2'
        elif sequence[i] == '2':
            result += '8'
        elif sequence[i] == '8':
            result += '5'
        elif sequence[i] == '5':
            result += '0'
    return result.
