def transform(sequence):
    zeros = sequence.count('0')
    ones = sequence.count('1')

    if zeros < ones:
        return sequence.replace('0', '0').replace('1', '1')
    elif zeros >= ones:
        return sequence.replace('0', '0').replace('1', '0').
