def transform(sequence):
    first_digit = sequence[0]
    new_sequence = first_digit
    for i in range(1, len(sequence)):
        if sequence[i] == first_digit:
            new_sequence += '0'
        else:
            new_sequence += sequence[i]
    return new_sequence
def transform(sequence):
    new_sequence = sequence.replace('1', '0')
    return new_sequence.
