def transform(sequence):
    transformed_sequence = ''
    count = 0

    for idx in range(len(sequence)):
        count += 1
        if idx == len(sequence) - 1 or sequence[idx] != sequence[idx + 1]:
            if count > 3:
                repeat_3_times = count // 3
                remaining_chars = count % 3
                transformed_sequence += sequence[idx] * 3 + sequence[idx] * remaining_chars + sequence[idx] * repeat_3_times
            else:
                transformed_sequence += sequence[idx] * count
            count = 0

    return transformed_sequence
