def transform(sequence):
    num_groups = sequence.split('0')
    transformed_sequence = '0' * len(num_groups[0])
    for i in range(1, len(num_groups)):
        if i != len(num_groups)-1:
            transformed_sequence += '0' + num_groups[i]
        else:
            transformed_sequence += '0' + '0'.join(num_groups[i][1:])
    return transformed_sequence

