def transform(sequence):
    first_digit = ''
    count = 0
    transformed_seq = []

    for digit in sequence:
        if digit != first_digit:
            transformed_seq.extend([first_digit]*min(count, 4))
            first_digit = digit
            count = 1
        else:
            count += 1

    transformed_seq.extend([first_digit]*min(count, 4))

    return ''.join(transformed_seq)
