def transform(sequence):
    new_sequence = list(sequence)
    zeros_count = sequence.count('0')
    num_nines = sequence.count('9')
    for i in range(len(new_sequence)):
        if new_sequence[i] == '0':
            new_sequence[i] = ''
    new_sequence = ['0'] * zeros_count + new_sequence

    index = 0
    for _ in range(num_nines):
        if new_sequence[index] == '9':
            index += 1
        new_sequence.insert(index, '9')
        index += 1

    return ''.join(new_sequence)
