def transform(sequence):
    zeros = sum(1 for i in sequence if i == '0')
    num_count = [0] * 10
    for i in sequence:
        num_count[int(i)] += 1
    transformed = "0" * zeros
    for i in range(1, 10):
        transformed += str(i) * num_count[i]
    return transformed + "0" * zeros

