def transform(sequence):
    first_nonzero = next(i for i, x in enumerate(sequence) if x != "0")
    first_zero_after_first_nonzero = next((i for i, x in enumerate(sequence) if x == "0" and i > first_nonzero), None)
    if first_zero_after_first_nonzero:
        return sequence[:first_zero_after_first_nonzero] + "0" + sequence[first_zero_after_first_nonzero:]
    else:
        return sequence

