def transform(sequence):
    zeros_count = 0
    transformed_sequence = ""

    for i in range(len(sequence)):
        if sequence[i] == '0':
            zeros_count += 1
        else:
            transformed_sequence += '0' * zeros_count + sequence[i]
            zeros_count = 0

    return transformed_sequence

