def transform(sequence):
    ones_indices = [i for i, x in enumerate(sequence) if x == '1']
    zeros_indices = [i for i, x in enumerate(sequence) if x == '0']
    new_sequence = list(sequence)
    for i in ones_indices:
        new_sequence[i] = '0'
    for i in zeros_indices:
        new_sequence[i] = '1'
    return "".join(new_sequence)
