def transform(sequence):
    count = {}
    transformed_sequence = ''

    for num in sequence:
        count[num] = count.get(num, 0) + 1

    for num in sequence:
        if num == '0':
            transformed_sequence += '0'
        else:
            transformed_sequence += num * max(0, count[num]-1)

    return transformed_sequence

