def transform(sequence):
    seq_list = list(sequence)
    last_num = seq_list[-1]
    new_sequence = []
    for num in seq_list:
        if num == '0' and last_num != '9':
            new_sequence.append('0')
        else:
            new_sequence.append('4')
    new_sequence[-1] = '9'
    return "".join(new_sequence)
