def transform(sequence):
    transformed = ""
    for i in range(len(sequence)):
        if sequence[i] == "0":
            transformed += "0"
        else:
            count = 1
            for j in range(i+1, len(sequence)):
                if sequence[j] == sequence[i]:
                    count += 1
                else:
                    break
            transformed += sequence[i]*count
    return transformed
