def transform(sequence):
    mid_idx = int(len(sequence) / 2)
    first_half = sequence[:mid_idx]
    second_half = sequence[mid_idx:]
    new_second_half = second_half.replace('0', '1')
    return first_half + new_second_half + '4' + '0' + '0' * (len(sequence) - mid_idx - 2)

