def transform(sequence):
    new_sequence = list(sequence)
    for i in range(len(sequence)):
        if new_sequence[i] == '0':
            count = 1
            for j in range(i + 1, len(sequence)):
                if new_sequence[j] == '0':
                    count += 1
                else:
                    new_sequence[i] = '0'
                    new_sequence[j] = str(count)
                    break
    return ''.join(new_sequence)
