def transform(sequence):
    transformed_seq = ''
    for i in range(len(sequence)):
        if i == sequence.index(max(sequence)):
            transformed_seq += str(int(sequence[i]) - 1)
        elif i == sequence.index(min(sequence)):
            transformed_seq += str(int(sequence[i]) + 1)
        else:
            transformed_seq += sequence[i]
    return transformed_seq
