def transform(sequence):
    mapping = {'0': '0', '1': '1', '2': '2', '3': '3'}
    seq_split = [sequence[i:i+4] for i in range(0, len(sequence), 4)]
    transformed_seq = []

    for group in seq_split:
        new_group = []
        for i, num in enumerate(group):
            if i == 0:
                new_group.append(mapping[num])
            else:
                new_group.append(str(int(num) * 3))
        transformed_seq.append(''.join(new_group))

    return ''.join(transformed_seq)

