def transform(sequence):
    count = 0
    transformed_sequence = ''
    for digit in sequence:
        if digit == '0':
            transformed_sequence += '0'
        else:
            count += int(digit)
            transformed_sequence += str(2 * int(digit))
    for i in range(count):
        transformed_sequence = transformed_sequence.replace('0', '6', 1)
    return transformed_sequence
