def transform(sequence):
    split_sequence = [sequence[i:i+4] for i in range(0, len(sequence), 4)]
    transformed_sequence = [split_sequence[0]]
    for i in range(1, len(split_sequence)):
        if split_sequence[i-1][0] == '0' or split_sequence[i][0] == '0':
            transformed_sequence.append('0'*4)
        else:
            transformed_sequence.append(split_sequence[i])
    return ''.join(transformed_sequence) + '0'*(len(sequence) - len(transformed_sequence)*4)

