    for i in range(len(sequence)):
        if sequence[i] != "0":
            transformed_sequence += sequence[i]
        else:
            next_non_zero = next((j for j in range(i, len(sequence)) if sequence[j] != "0"), None)
            if next_non_zero:
                transformed_sequence += sequence[next_non_zero]
            else:
                transformed_sequence += "0"
    return transformed_sequence
