def transform(sequence):
    transformed = []
    previous_digit = ''
    count = 0
    for digit in sequence:
        if digit != previous_digit:
            transformed.append(digit)
            previous_digit = digit
            count = 1
        else:
            count += 1
            if count == 7:
                for _ in range(6):
                    transformed.append(digit)
                count = 1
            else:
                transformed.append(digit)
    return ''.join(transformed)

