def transform(sequence):
    transformed_seq = ''
    for i in range(len(sequence)):
        if sequence[i] == '0':
            transformed_seq += '0'
        else:
            count = sequence.count(sequence[i])
            transformed_seq += sequence[i]*count
    return transformed_seq
