def transform(sequence):
    count = 0
    result = []
    for i in range(len(sequence)):
        if sequence[i] == '1':
            count += 1
        else:
            result.append('1' * count)
            result.append(sequence[i])
            count = 0
    result.append('1' * count)
    return ''.join(result)
