def transform(sequence):
    transformed_sequence = ""
    count = 0
    prev = sequence[0]
    for i in range(len(sequence)):
        if sequence[i] == prev:
            count += 1
        else:
            transformed_sequence += prev * count
            prev = sequence[i]
            count = 1
    transformed_sequence += prev * count
    return transformed_sequence

def transform(sequence):
    transformed_sequence = []
    for i in range(len(sequence)):
        if sequence[i] == '1' and (i == 0 or sequence[i-1] != '1'):
            transformed_sequence.append('1')
        elif sequence[i] == '0':
            transformed_sequence.append('0')
        else:
            transformed_sequence.append(sequence[i])
    return ''.join(transformed_sequence)
