def transform(sequence):
    mapping = {'0': '0', '8': '8'}
    new_seq = ''
    for i in range(len(sequence)):
        if i > 0 and sequence[i] == sequence[i-1]:
            new_seq += new_seq[-1]
        elif sequence[i] in mapping:
            new_seq += mapping[sequence[i]]
        else:
            new_seq += '5'
    return new_seq
