def transform(sequence):
    new_sequence = ''
    for i in range(len(sequence)):
        if sequence[i] == '7':
            if i > 0 and sequence[i-1] == '0':
                new_sequence += '4'
            else:
                new_sequence += '7'
        else:
            new_sequence += '0'
    return new_sequence
