def transform(sequence):
    transformed_sequence = sequence[:2]
    for i in range(2, len(sequence)-2):
        if sequence[i-2] == sequence[i+2]:
            transformed_sequence += '2'
        else:
            transformed_sequence += sequence[i]
    transformed_sequence += sequence[-2:]
    return transformed_sequence

def transform(sequence):
    transformed_sequence = ''

    for i in range(len(sequence)):
        if sequence[i] == '3':
            if i > 0 and sequence[i-1] == '3':
                transformed_sequence += '2'
            else:
                transformed_sequence += '3'
        else:
            transformed_sequence += sequence[i]

    return transformed_sequence

def transform(sequence):
    transformed_sequence = ''
    for i in range(len(sequence)):
        if i == 0:
            transformed_sequence += sequence[i]
