def transform(sequence):
    transformed_sequence = ""
    for i in range(len(sequence)):
        if sequence[i] != '0':
            transformed_sequence += sequence[i]
        else:
            if i > 0 and sequence[i-1] != '0':
                prev_digit = int(sequence[i-1])
                next_nonzero_index = i
                while next_nonzero_index < len(sequence) and sequence[next_nonzero_index] == '0':
                    next_nonzero_index += 1
                next_digit = int(sequence[next_nonzero_index])
                transformed_sequence += str((prev_digit + next_digit) // 2) * (next_nonzero_index - i + 1)
            else:
                transformed_sequence += '0'
    return transformed_sequence
