def transform(sequence):
    transformed_sequence = ''
    for i in range(0, len(sequence), 4):
        block = sequence[i:i+4]
        count = block.count('9')
        if count % 2 == 0:
            transformed_block = '0' + block.replace('9', '1')
        else:
            transformed_block = '1' + block.replace('9', '7')
        transformed_sequence += transformed_block
    return transformed_sequence
